#!/bin/bash yum update -y \nyum install python3 -y \ncurl -O https://bootstrap.pypa.io/get-pip.py \npython3 get-pip.py --user \nexport PATH=~/.local/bin:$PATH \nsource ~/.bash_profile \npip3 install awscli --upgrade --user \nmkdir /opt/appdata \ncd /opt/appdata \naws s3api get-object --bucket=${ArtifactsBucket} --key=aj_ec2_setup.sh aj_ec2_setup.sh \nchmod 755 aj_ec2_setup.sh \nsh ./aj_ec2_setup.sh ${ArtifactsBucket} \n